mapper.update:

UPDATE category 
SET category_name = #{category_name}
	, category_desc = #{category_desc}
	, category_update = to_char(sysdate, 'YYYY-MM-DD HH24:MI:SS')
	, category_updater = #{category_updater}
	, upper_category_idx = #{upper_category_idx}



	, upper_category_name = (SELECT category_name FROM category WHERE category_idx=#{upper_category_idx})



WHERE category_idx= #{category_idx}


UPDATE category
SET category_deldate = to_char(sysdate, 'YYYY-MM-DD HH24:MI:SS')
	, category_deleter = #{category_deleter}
WHERE category_idx= #{category_idx}

mapper.insert:

	SELECT category_seq.nextval AS category_idx FROM dual


INSERT INTO category(
	category_idx
	, category_name
	, category_desc
	, upper_category_idx


, upper_category_name


	, category_regdate
	, category_register
) VALUES (
	#{category_idx}
	, #{category_name}
	, #{category_desc}
	, #{upper_category_idx}


, (SELECT category_name FROM category WHERE category_idx=#{upper_category_idx})


	, to_char(sysdate, 'YYYY-MM-DD HH24:MI:SS')
	, #{category_register}
)
